import ContentModel from '../database/models/Content';
import HistoryModel from '../database/models/History';
import { Op } from 'sequelize';

class ContentService {
  constructor(
    private contentModel: typeof ContentModel,
    private historyModel: typeof HistoryModel
  ) {}

  async create(content: ContentModel): Promise<ContentModel> {
    const createdContent = await this.contentModel.create(content as any);
    return this.historyModel.create({
      content_id: createdContent.id,
      titulo: createdContent.titulo,
      corpo: !content.corpo ? 'Campo deixado vazio pelo autor.' : content.corpo
    }).then(() => createdContent );
  }

  async findAll(): Promise<ContentModel[]> {
    const contents = await this.contentModel.findAll();
    return contents;
  }

  async findByPk(id: number): Promise<ContentModel> {
    const content = await this.contentModel.findByPk(id);

    if (!content) throw new Error('contentNotFound');

    return content;
  }

  async findByName(titulo: string): Promise<ContentModel[]> {
    const contents = await this.contentModel.findAll({
      where: {
        titulo: {
          [Op.like]: `%${titulo}%`
        }
      }
    });
    if(!contents.length) throw new Error('contentsNotFound');

    return contents;
  }

  async update(id: number, content: ContentModel): Promise<object> {
    const lastcontent = await this.contentModel.findByPk(id);
    if (!lastcontent) throw new Error('contentNotFound');

    return this.contentModel.update(content, { where: { id } }).then(async () => {
      this.historyModel.create({
        content_id: id,
        titulo: content.titulo,
        corpo: !content.corpo ? 'Campo deixado vazio pelo autor.' : content.corpo
      });
      return { message: 'Conteúdo atualizado com sucesso!' }
    }).catch((err) => {throw new Error(err.message)});
  }

  async delete(id: number): Promise<object> {
    const lastcontent = await this.contentModel.findByPk(id);
    if (!lastcontent) throw new Error('contentNotFound');
    return this.contentModel.destroy({ where: { id } }).then(() => {
      return { message: 'Conteúdo excluído com sucesso!' }
    }).catch((err) => {throw new Error(err.message)});
  }
}

export default ContentService;

import HistoryModel from '../database/models/History';

class HistoryService {
  constructor(
    private historyModel: typeof HistoryModel
  ) {}

  async findHistoryById(id: number): Promise<HistoryModel[]> {
    const history = await this.historyModel.findAll({
      where: { content_id: id },
      order: [
        ['created_at', 'ASC'],
      ]       
    });
    return history;
  }


}

export default HistoryService;

import { NextFunction, Request, Response } from 'express';
import ContentService from '../services/Content.service';
import HistoryService from '../services/History.service';

class ContentController {
  constructor(
    private contentService: ContentService,
    private historyService: HistoryService
  ) {}

  async findAll(_req: Request, res: Response): Promise<Response> {
    const contents = await this.contentService.findAll();
    return res.status(200).json(contents);
  }

  async findHistory(req: Request, res: Response, next: NextFunction): Promise<Response | void> {
    try {
      const { id } = req.params;
      const content = await this.contentService.findByPk(Number(id));
      const history = await this.historyService.findHistoryById(Number(id));
      return res.status(200).json({ content, history });
    } catch(err) {
      return next(err);
    }
  }

  async findByTitle(req: Request, res: Response, next: NextFunction): Promise<Response | void>{
    try {
      const { title } = req.query;
      const contents = await this.contentService.findByName(title as string);
      return res.status(200).json(contents);
    } catch(err) {
      return next(err);
    }
  }

  async create(req: Request, res: Response): Promise<Response> {
    const content = await this.contentService.create(req.body);
    return res.status(201).json(content);
  }

  async update(req: Request, res: Response, next: NextFunction): Promise<Response | void> {
    try {
    const { id } = req.params;
    const message = await this.contentService.update(Number(id), req.body);
    return res.status(200).json(message);
    } catch(err) {
    return next(err);
    }
  }

  async delete(req: Request, res: Response, next: NextFunction): Promise<Response| void> {
    try {
      const { id } = req.params;
      const message = await this.contentService.delete(Number(id));
      return res.status(200).json(message);
    } catch(err) {
      return next(err)
    }
  }
}

export default ContentController;

import { NextFunction, Request, Response } from 'express';

export default function contentVAlidation(req: Request, res: Response, next: NextFunction) {
  const { body } = req;
  if (!body.titulo || body.titulo.length < 5) throw new Error('invalidFields');
  next();
}

import { NextFunction, Request, Response } from 'express';

type errInformation = {
  [key: string]: { message: string, code: number }
};

const errorsInformation: errInformation = {
  contentNotFound: { message: 'O conteúdo com o Id buscado não existe!', code: 404 },
  invalidFields: { message: 'O título é obrigatório e deve possuir mais de 4 caracteres!', code: 400 },
  contentsNotFound: { message: 'Não existem conteúdos com esse título!', code: 404 },
};

export default function errorMiddleware(
  error: Error,
  _req: Request,
  res: Response,
  _next: NextFunction
) {
  const { message } = error;
  if(errorsInformation[message]) {
    const errorType = errorsInformation[message];
    return res.status(errorType.code).json({ message: errorType.message });
  }
  return res.status(500).json({ message });
}

